import React, { useState, useEffect, useContext } from "react";
import { Route, Redirect, Routes, useNavigate, useLocation} from 'react-router-dom';
import { SidebarContext } from "./context/SidebarContext.js";
import Settings, {UserSettings, ReceiptSettings} from './pages/app/Settings.js'
import Login from './pages/auth/Login.js'
import Register from './pages/auth/Register.js'
import Sidebar from "./pages/app/Sidebar";
import Navbar from './pages/nav/Navbar.js'
import Wallet from './pages/app/Wallet.js'
import Landing from "./pages/auth/Landing.js";
import PrivateRoute from "./PrivateRoute/index.js";

const App = () => {

  const [sidebarState, dispatch] = useContext(SidebarContext);
  const changeDash = (type) => {dispatch({type})};
  let navigate = useNavigate();

  const { pathname } = useLocation();
  const settingsDashPages = ['/settings'];
  const walletDashPages = ['/wallet', '/shared'];

  useEffect(() => {
    if(walletDashPages.includes(pathname)){
      dispatch({type: 'SHOW_BAR'})
      dispatch({type: 'WALLET_BAR'});
    }
    else if(settingsDashPages.includes(pathname)){
      dispatch({type: 'SHOW_BAR'})
      dispatch({type: 'SETTINGS_BAR'});
    } else {
      dispatch({type: 'HIDE_BAR'});
    }
  }, [pathname]);

  return (
    <div className="App">
      <div className="app-container">
        <div className="nav">
          <Navbar/>
        </div>
        <div className="dashboard">
          {sidebarState.visible && (
            <Sidebar/>
          )}
        </div>
        <div className={sidebarState.visible ? "app-content-dashboard" : "app-content"}>
          <Routes>
            <Route path="/login" element={<Login/>}/>
            <Route path="/register" element={<Register/>}/>
            <Route path="/home" element={<Landing/>}/>
            <Route path="/wallet" element={<PrivateRoute><Wallet/></PrivateRoute> } />
            <Route path="/settings" element={<PrivateRoute><Settings/></PrivateRoute> } />
            <Route path="/settings/user" element={<PrivateRoute><UserSettings/></PrivateRoute> } />
            <Route path="/settings/receipts" element={<PrivateRoute><ReceiptSettings/></PrivateRoute> } />
          </Routes>
        </div>
      </div>
    </div>
  );
}

export default App;
