import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { Navigate } from 'react-router-dom';
import Cookies from 'js-cookie';

const RegisterPreVerify = (props) =>
{

    const [email, setEmail] = useState('');
    const [confirmClicked, setConfirmClicked] = useState(false);
    const [emailVerified, setEmailVerified] = useState(false);
    let navigate = useNavigate();

    const checkVerification = async () => {
        if (confirmClicked) {
            axios.get('http://localhost:8000/verify/', {
                params: {
                    'token': 'null',
                    'method': 'CHECK',
                    'email': email
                }
            })
            .then(response => {
                if (response.data.status === 'OK') {
                    setEmailVerified(true);
                    navigate('/register');
                }
            })
            .catch(error => {
                console.error("Error", error)
            })
        }
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setConfirmClicked(true);

        checkVerification();
        console.log(confirmClicked);
        try {
            const response = await axios.post('http://localhost:8000/sendEmail/', {
                'email':email,
            });
            console.log(response.data);
        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        const intervalID = setInterval(checkVerification, 3000);

        return () => clearInterval(intervalID);
    }, [email, confirmClicked]);

    return (
        <div className='auth-form-container'>
            <form className='register-pre-verify' onSubmit={handleSubmit}>
                <h4>Welcome to the inbox built exclusively for managing receipts, no email sorting involved.</h4>
                <p>First, lets confirm your email address.</p>
                {confirmClicked && (
                    <h4 className='confirm-message'>{!emailVerified? "Email sent!":"Great! your email address has been verified, redirecting..."}</h4>
                )}
                <i className='confirm-icon-check' class={emailVerified ? "fa fa-check":"fa fa-times"} aria-hidden="true"></i>
                <label htmlFor="email">Email</label>
                <input value={email} onChange={(e) => {setEmail(e.target.value); setConfirmClicked(false);}} type="email" placeholder="youremail@domain.com" />
                <button type="submit">Confirm</button>
            </form>
        </div>
    )
}

const Register = (props) => 
{
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [error, setError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');

    const { dispatch } = useContext(AuthContext);
    let navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/register/', {
                'email':email,
                'first_name':firstName,
                'last_name':lastName,
                'password':pass,
            });

            console.log(response.data);
            try {
                const response = await axios.post('http://localhost:8000/authenticate/', {
                    'email':email,
                    'password':pass,
                });

                if (response.data.status === 'OK') {
                    Cookies.set('token', response.data.token, {expires: 7});
                    Cookies.set('data', JSON.stringify(response.data.data), {expires: 7});
                    dispatch({type: 'LOGIN', payload: response.data});
                    navigate("/wallet");
                    console.log(JSON.stringify(response.data.data));
                    setError(false);
                    setErrorMsg('');
                }
            } catch (error) {
                console.log(error);
                setError(true);
                setErrorMsg(error.response.data.message);
            }
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div className='auth-form-container'>
            <form className='register-form' onSubmit={handleSubmit}>
                <label htmlFor="firstName">First Name</label>
                <input value={firstName} onChange={(e) => setFirstName(e.target.value)} id="firstName" type="text" placeholder="John" />
                <label htmlFor="lastName">Last Name</label>
                <input value={lastName} onChange={(e) => setLastName(e.target.value)} id="lastName" type="text" placeholder="Doe" />
                <label htmlFor="email">Email</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@domain.com" />
                <label htmlFor="password">Password</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder='Password'/>
                <button type="submit">Register</button>
            </form>
            <button className='link-btn' onClick={() => navigate("/login")}>Already have an account? Log in here.</button>
        </div>
    );
}

export { RegisterPreVerify, Register };
