import React, { useState, useEffect, useContext } from "react";
import { Route, Redirect, Routes, useNavigate, useLocation} from 'react-router-dom';
import { DashContext } from "./context/DashContext.js";
import Login from './pages/auth/Login.js'
import Register from './pages/auth/Register.js'
import Dashboard from "./pages/app/Dashboard";
import Navbar from './pages/nav/Navbar.js'
import Profile from './pages/app/Profile.js'
import Wallet from './pages/app/Wallet.js'
import Landing from "./pages/auth/Landing.js";
import PrivateRoute from "./PrivateRoute/index.js";

const App = () => {
  const [dashboard, setDashboard] = useContext(DashContext);
  const toggleDash = () => {setDashboard(!dashboard)};
  let navigate = useNavigate();

  const { pathname }= useLocation();
  const dashPages = ['/wallet', '/shared'];

  useEffect(() => {
    if(dashPages.includes(pathname)){
      setDashboard(true);
    } else {
      setDashboard(false);
    }
  }, [pathname]);

  return (
    <div className="App">
      <div className="nav">
        <Navbar/>
      </div>
      <div className="dash">
        {dashboard && (
          <Dashboard/>
        )}
      </div>
      <div className={dashboard ? "app-content-dashboard" : "app-content"}>
        <Routes>
          <Route path="/login" element={<Login/>}/>
          <Route path="/register" element={<Register/>}/>
          <Route path="/home" element={<Landing/>}/>
          <Route path="/profile" element={<PrivateRoute><Profile/></PrivateRoute> } />
          <Route path="/wallet" element={<PrivateRoute><Wallet/></PrivateRoute> } />
        </Routes>
      </div>
    </div>
  );
}

export default App;
