import React, { useContext, useState } from 'react';
import axios from 'axios';
import { Route, Redirect, useNavigate, Navigate } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import Cookies from 'js-cookie';

const Login = (props) =>
{
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [isAuthenticated, setIsAuthenticated] = useState(!!Cookies.get('token'))
    const { dispatch } = useContext(AuthContext);
    let navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/login/', {
                'email':email,
                'password':pass,
            });

            if (response.data.status === 'success') {
                Cookies.set('token', response.data.token, { expires: 7});
                Cookies.set('firstName', response.data.firstName, { expires: 7});
                dispatch({type: 'LOGIN', payload: response.data});
                console.log(response.data);
                navigate("/dashboard");

            }
            else {
                console.log(response.data.message);
            }
        } catch (error) {
            console.log(error);
        }

    }

    return (
        <div className='auth-form-container'>
            <form className='login-form' onSubmit={handleSubmit}>
                <p>Welcome back.</p>
                <label htmlFor="email">Email</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@domain.com"/>
                <label htmlFor="password">Password</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder='Password'/>
                <button type="submit">Log In</button>
            </form>
            <button onClick={() => navigate("/register")}>Haven't made an account yet? Register here.</button>
        </div>
    );
}

export default Login;
