import React, { useEffect, useState, useContext } from "react";
import PopupWindow from "../auth/PopupWindow";
import Portal from "../../Util/Portal";
import axios from "axios";
import Cookies from "js-cookie";
import './wallet.css'


const Wallet = () => {

    const [contracts, setContracts] = useState([]);
    const [dropdownIndex, setDropdownIndex] = useState(null);
    const [shareIndex, setShareIndex] = useState(null);
    const [deleteIndex, setDeleteIndex] = useState(null);

    const toggleDropdown = (index) => {
        if(dropdownIndex === index) {
            setDropdownIndex(null);
        } else {
            setDropdownIndex(index);
        }
    }

    const togglePopupShare = (index) => {
        if(shareIndex === index) {
            setShareIndex(null);
        } else {
            setShareIndex(index);
        }
    }

    const togglePopupDelete = (index) => {
        if(deleteIndex === index) {
            setDeleteIndex(null);
        } else {
            setDeleteIndex(index);
        }
    }

    const editReceipt = (action, contractID, recipientID, sharedWith='') => {
        const token = Cookies.get('token');
        console.log(sharedWith);
        axios
        .post(
            'http://localhost:8000/edit/',
            {
              action: action,
              contractID: contractID,
              recipientID: recipientID,
              sharedWith: sharedWith
            },
            {
              headers: {
                Authorization: `Token ${token}`,
              },
            }
          )
          .then((response) => {
            console.log(response);
            fetchData();
            console.log("current version updated")
          })
          .catch((error) => {
            console.error('error: ', error);
          });
      };

    const fetchData = () => {
        const token = Cookies.get('token');
        axios.get('http://localhost:8000/get/', {
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then((response) => {
            console.log(response);
            setContracts(response.data.contracts);
            response.data.contracts.map((contract, index) => {
            console.log(contract);
            })
        })
        .catch((error) => {
            console.error('error: ', error);
        });
    }

    useEffect(() => {
        fetchData();
     }, []);

    return (
        <div>
            {
               (!contracts || contracts.length === 0) ? (
                    <p>No receipts found</p>
                ):(
                <div className="contractContainer">
                    {contracts.map((contract, index) => {
                        return (
                            <div className='contractBox' key={index}>
                                {deleteIndex === index && (
                                    <Portal>
                                        <PopupWindow label='Are you sure you want to delete this receipt?' buttonLeft='Cancel' buttonRight='Confirm' 
                                        confirm={() => editReceipt('DELETE', contract.contract.contractID, contract.contract.recipientID)} toggleVisibility={() => togglePopupDelete()}/>
                                    </Portal>
                                )}
                                {shareIndex === index && (
                                    <Portal>
                                        <PopupWindow label='Share' buttonLeft='Cancel' buttonRight='Share' 
                                        confirm={(output) => editReceipt('SHARE', contract.contract.contractID, contract.contract.recipientID, output)} toggleVisibility={() => togglePopupShare()} 
                                        textfeild={true} textfeildCaption='Enter target user email address' textfeildLabel='email@domain.com' textfeildType='email'/>
                                    </Portal>
                                )}
                                <div className="contract-menu">
                                    <div>
                                        {contract.contract.flagged && (
                                            <i className="info-icon" class="fa-solid fa-flag"></i>
                                        )}
                                        {contract.contract.sharedWith.length > 0 && (
                                            <i className="info-icon" class="fa-solid fa-users"></i>
                                        )}
                                    </div>
                                    <div>
                                        <i className="contract-icon" class="fa-solid fa-share" onClick={() => {togglePopupShare(index); console.log(contract.contract.sharedWith)}}></i>
                                        <i className="contract-icon" class="fa-solid fa-ellipsis-vertical" onClick={() => toggleDropdown(index)}></i>
                                    </div>
                                </div>
                                {dropdownIndex === index && (
                                        <div className="contract-dropdown">
                                            <ul>
                                                <li onClick={() => editReceipt('FLAG', contract.contract.contractID, contract.contract.recipientID)}>
                                                    <i class="fa-solid fa-flag"></i>
                                                    <span>Flag</span>
                                                </li>
                                                <li>
                                                    <i class="fa-solid fa-trash" onClick={() => togglePopupDelete()}></i>
                                                    <span>Delete</span>
                                                </li>
                                            </ul>
                                        </div>
                                )}
                                <p className="issuerID">{contract.contract.issuerID}</p>
                                <p className="itemTag">Items:</p>
                                <div className="itemList">
                                    <ul>
                                        {
                                            contract.items.map((item, itemIndex) => {
                                                return (
                                                    <li key={itemIndex}>
                                                        {item.name}
                                                        <span>: </span>
                                                        {item.price}
                                                    </li>
                                                )
                                            })
                                        }
                                    </ul>
                                </div>
                                <div className="contractInfo">
                                    <p className="datePurchased">
                                        {contract.contract.dateCreated}
                                    </p>
                                </div>
                            </div>
                        )
                    })}
                </div>
                )
            }
        </div>
    );
}

export default Wallet;
