import React, { useEffect, useState } from "react";
import "./styles/landing.css"
import { motion, scroll } from "framer-motion";
import { Link } from "react-router-dom";
import ProgressWheel from "./ui/ProgressWheel";

const Landing = () => {

    const [hasScrolled, setHasScrolled] = useState(false);
    const [scrollPos, setScrollPos] = useState(0);

    function calcDisplay() {
            if (scrollPos < 0) {
                setScrollPos(0);
            }
            else if (scrollPos > 0){
                setScrollPos(0);
            }
            else if (scrollPos > 100) {
                setScrollPos(0);
            }
        }

    useEffect(() => {
        const handleScroll = (event) => {
            setScrollPos(prevScrollPos => prevScrollPos + event.deltaY / 100);
            setHasScrolled(true);
        }

        window.addEventListener('wheel', handleScroll);

        return () => {
            window.removeEventListener('wheel', handleScroll);
        }
    }, []);

    return (
        <div className="main-container">
            <ProgressWheel scrollValue={scrollPos} maxValue={100} />
            <div className="main-splash-text">
                <h1>One place for all of your digital receipts, absolutely zero email sorting involved.</h1>
                <h1>{scrollPos}</h1>
            </div>

            <Link classname='main-trial-button' to='/preregister'><h3>Try OneReturn for personal use</h3></Link>
        </div>
    );

}

export default Landing;
