import React, {useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Cookies from "js-cookie";

const Contract = () => {

    const { contractID } = useParams();
    const [contracts, setContracts] = useState([]);

    const editReceipt = (action, contractID, recipientID, sharedWith='', itemName='', returner='') => {
        const token = Cookies.get('token');
        console.log(sharedWith);
        axios
        .post(
            'http://localhost:8000/edit/',
            {
              action: action,
              contractID: contractID,
              recipientID: recipientID,
              sharedWith: sharedWith,
              itemName: itemName,
              returner: returner
            },
            {
              headers: {
                Authorization: `Token ${token}`,
              },
            }
          )
          .then((response) => {
            console.log(response);
            fetchData();
            console.log("current version updated")
          })
          .catch((error) => {
            console.error('error: ', error);
          });
      };


    const fetchData = () => {
        const token = Cookies.get('token');
        axios.get('http://localhost:8000/get/', {
            params: {
                type: 'SINGLE',
                query: contractID
            },
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then((response) => {
            console.log(response);
            setContracts(response.data.contracts)
        })
        .catch((error) => {
            console.error('error: ', error);
        });
    }

    useEffect(() => {
        fetchData();
     }, []);

     return (
        <div className="contract-container">
          {contracts.map((contract, index) => (
                <div key={index}>
                    <h2 className="contract-id-label">{contract.contract.contractID}</h2>
                    <h3>{contract.contract.merchantID}</h3>
                    <h3>{contract.contract.merchantLocNumber}</h3>
                    <h3>{contract.contract.merchantAddress}</h3>
                    <h3>{contract.contract.recipientID}</h3>
                <div className="item-list-container">
                    <div className="item-list">
                        <h2>Items:</h2>
                        {contract.items.map((item, itemIndex) => (
                            <div key={itemIndex}>
                                <i class="fa-solid fa-arrow-left fa-2xl" 
                                onClick={() => editReceipt('RETURN', contract.contract.contractID, contract.contract.recipientID, '', item.name, data.uid)}></i>
                                <h3>{item.name}</h3>
                                {item.returned && (
                                <div>
                                    <p className="returned-label">RETURNED</p>
                                    <p className="returned-funds">-{item.price}</p>
                                </div>
                                )}
                            </div>
                        ))}
                    </div>
              </div>
              <div className="transaction-history-container">
                    <h2>History:</h2>
                    {contract.contract.transactionHistory.map((i, iIndex) => (
                            <div key={iIndex}>
                                <h3>{i}</h3>
                            </div>
                    ))}
              </div>
            </div>
          ))}
        </div>
    );
}

export default Contract;
