import React, { useState, useEffect, useContext, ReactDOM } from "react";
import { AuthContext } from "../../context/AuthContext.js";
import { Link, useNavigate } from "react-router-dom";
import {FaBars, FaTimes} from "react-icons/fa";
import Cookies from 'js-cookie';
import axios from "axios";
import './navbar.css';

const Navbar = () => {

    const { user, dispatch } = useContext(AuthContext);
    const [isOpen, setIsOpen] = useState(false);
    const isAuthenticated = !!user;
    const firstName = Cookies.get('firstName');
    const token = Cookies.get('token');
    let navigate = useNavigate();

    useEffect(() => {
        let dropdownPortal = null;

        if (isOpen) {
          dropdownPortal = document.createElement('div');
          dropdownPortal.classList.add('dropdown-portal');
          document.getElementById('portal-root').appendChild(dropdownPortal);
          ReactDOM.render(dropdown, dropdownPortal);
        }

        return () => {
          if (dropdownPortal) {
            ReactDOM.unmountComponentAtNode(dropdownPortal);
            dropdownPortal.remove();
          }
        };
      }, [isOpen]);

    const toggleDropDown = () => {
        setIsOpen(!isOpen);
    }

    const dropdown = (
        <div className="dropdown">
            <ul>
                <li><Link to="/console">Console</Link></li>
                <li><Link to="/profile">Profile</Link></li>
                <li><button onClick={(event) => {handleLogout(); toggleDropDown()}}>Logout</button></li>
            </ul>
        </div>
    );

    const handleLogout = async (e) => {

        const response = await axios.post('http://localhost:8000/logout/', {},{
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then(response => {
            console.log(response.data);
        })
        .catch(error => {
            console.error('There was an error', error);
        });

        Cookies.remove('token');
        Cookies.remove('firstName');

        dispatch({ type: 'LOGOUT' });
        navigate('/login');
    }

    return (
        <nav className="nav">
            <h3>OneReturn</h3>
            <ul>
                {!isAuthenticated && (
                    <>
                        <li><Link to="/login">Login</Link></li>
                        <li><Link to="/register">Register</Link></li>
                    </>
                )}
                {isAuthenticated && (
                    <>
                        <p>Welcome, {firstName}</p>
                        <button onClick={toggleDropDown}>
                            <FaBars/>
                        </button>
                        {isOpen && ReactDOM.createPortal(dropdown, document.getElementById('portal-root'))}
                    </>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
