import React, { useState } from 'react';
import axios from 'axios';

const Register = (props) => 
{
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8000/register/', {
                'email':email,
                'first_name':firstName,
                'last_name':lastName,
                'password':pass,
            });

            console.log(response.data);
            try {
                const response = await axios.post('http://localhost:8000/login/', {
                    'email':email,
                    'password':pass,
                });

                if (response.data.status === 'success') {
                    localStorage.setItem('token', response.data.token);
                    setIsAuthenticated(true);
                    console.log(response.data.message);
                    navigate("/console");

                }
                else {
                    console.log(response.data.message);
                }
            } catch (error) {
                console.log(error);
            }
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <div className='auth-form-container'>
            <form className='register-form' onSubmit={handleSubmit}>
                <label htmlFor="firstName">First Name</label>
                <input value={firstName} onChange={(e) => setFirstName(e.target.value)} id="firstName" type="text" placeholder="John" />
                <label htmlFor="lastName">Last Name</label>
                <input value={lastName} onChange={(e) => setLastName(e.target.value)} id="lastName" type="text" placeholder="Doe" />
                <label htmlFor="email">Email</label>
                <input value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="youremail@domain.com" />
                <label htmlFor="password">Password</label>
                <input value={pass} onChange={(e) => setPass(e.target.value)} type="password" placeholder='Password'/>
                <button type="submit">Register</button>
            </form>
            <button className='link-btn' onClick={() => props.onFormSwitch('login')}>Already have an account? Log in here.</button>
        </div>
    );
}

export default Register;
