import React, { useEffect, useState } from "react";
import axios from "axios";
import Cookies from "js-cookie";
import './wallet.css'
import Dashboard from "./Dashboard";


const Wallet = () => {

    const [contracts, setContracts] = useState([]);
    const [dropdown, setDropdown] = useState(false);

    const toggleDropdown = () => {setDropdown(!dropdown)}

    const editReceipt = (action, contractID, recipientID) => {
        const token = Cookies.get('token');
        axios
          .post(
            'http://localhost:8000/edit/',
            {
              action: action,
              contractID: contractID,
              recipientID: recipientID,
            },
            {
              headers: {
                Authorization: `Token ${token}`,
              },
            }
          )
          .then((response) => {
            console.log(response);
            fetchData();
          })
          .catch((error) => {
            console.error('error: ', error);
          });
      };

    const fetchData = () => {
        const token = Cookies.get('token');
        axios.get('http://localhost:8000/get/', {
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then((response) => {
            console.log(response);
            setContracts([...contracts, ...response.data.contracts]);
            response.data.contracts.map((contract, index) => {
            console.log(contract);
            })
        })
        .catch((error) => {
            console.error('error: ', error);
        });
    }

    useEffect(() => {
        fetchData();
     }, []);

    return (
        <div>
            <div className="dashboard">
                <Dashboard/>
            </div>
            {
               (!contracts || contracts.length === 0) ? ( 
                    <p>No contracts found</p>
                ):(
                <div className="contractContainer">
                    {contracts.map((contract, index) => {
                        return (
                            <div className='contractBox' key={index}>
                                <div className="contract-menu">
                                    {contract.contract.flagged && (
                                        <i className="info-icon" class="fa-solid fa-flag"></i>
                                    )}
                                    <i className="contract-icon" class="fa-solid fa-share" onClick={() => editReceipt('SHARE')}></i>
                                    <i className="contract-icon" class="fa-solid fa-ellipsis-vertical" onClick={toggleDropdown}></i>
                                    {dropdown && (
                                        <div className="contract-dropdown">
                                            <ul>
                                                <li onClick={() => editReceipt('FLAG', contract.contract.contractID, contract.contract.recipientID)}>
                                                    <i class="fa-solid fa-flag"></i>
                                                    <span>Flag</span>
                                                </li>
                                                <li>
                                                    <i class="fa-solid fa-trash"></i>
                                                    <span>Delete</span>
                                                </li>
                                            </ul>
                                        </div>
                                    )}
                                </div>
                                <p className="issuerID">{contract.contract.issuerID}</p>
                                <p className="itemTag">Items:</p>
                                <div className="itemList">
                                    <ul>
                                        {
                                            contract.items.map((item, itemIndex) => {
                                                return (
                                                    <li key={itemIndex}>
                                                        {item.name}
                                                        <span>: </span>
                                                        {item.price}
                                                    </li>
                                                )
                                            })
                                        }
                                    </ul>
                                </div>
                                <div className="contractInfo">
                                    <p className="datePurchased">
                                        {contract.contract.dateCreated}
                                    </p>
                                </div>
                            </div>
                        )
                    })}
                </div>
                )
            }
        </div>
    );
}

export default Wallet;
