import React, { useEffect, useRef } from 'react';
import { motion, useAnimation } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import "./styles/landing.css";

const Landing = () => {
    let navigate = useNavigate();

    // Function to handle navigation
    const handleNavigate = (path) => {
        navigate(path);
    };

    // Custom hook to handle the animation on scroll
    const useScrollAnimation = () => {
        const controls = useAnimation();
        const ref = useRef(null);

        useEffect(() => {
            const observer = new IntersectionObserver(
                ([entry]) => {
                    if (entry.isIntersecting) {
                        controls.start('visible'); // Start the animation when the section is in view
                    }
                },
                {
                    rootMargin: '-100px 0px',
                    threshold: 0.5
                }
            );

            if (ref.current) {
                observer.observe(ref.current); // Attach observer to the section
            }

            return () => {
                if (ref.current) {
                    observer.unobserve(ref.current); 
                }
            };
        }, [controls, ref]);

        return [ref, controls]; 
    };

    const [mainRef, mainControls] = useScrollAnimation();
    const [businessRef, businessControls] = useScrollAnimation();
    const [pricingRef, pricingControls] = useScrollAnimation();

    // Animation variants for fading and sliding effect
    const sectionVariants = {
        hidden: { opacity: 0, y: 50 },
        visible: { opacity: 1, y: 0, transition: { duration: 0.5 } }
    };

    return (
        <div className="container-main">
            <motion.div className="main-landing-section"
                ref={mainRef}
                variants={sectionVariants}
                initial="hidden"
                animate={mainControls}
            >
                <h1>One place for all of your digital receipts, absolutely zero email sorting involved.</h1>
                <p>Imagine never having to sort through your email for receipts again. With OneReturn, that reality is now. Explore how OneReturn integrates smoothly with multiple platforms to enhance your organizational experience.</p>
                <button className="main-trial-button" onClick={() => handleNavigate('/preregister')}>
                    Try OneReturn for personal use
                </button>
            </motion.div>

            <motion.div className="for-business"
                ref={businessRef}
                variants={sectionVariants}
                initial="hidden"
                animate={businessControls}
            >
                <h1>Use OneReturn for your business</h1>
                <div className="for-business-section">
                    <h3>
                        The average person has 1.75 email accounts, the average office worker receives 121 emails each day and, as of 2022, over 162 billion spam emails were sent everyday;
                        odds are: your customer's digital receipts are getting lost.
                    </h3>
                    <p>Adding more detailed information here can illustrate the pain points of lost receipts, the clutter of email inboxes, and the challenges businesses face without a proper management system. Discuss the benefits of adopting OneReturn for businesses, including improved customer satisfaction, easier returns management, and better data organization.</p>
                </div>
            </motion.div>

            <motion.div className="pricing"
                ref={pricingRef}
                variants={sectionVariants}
                initial="hidden"
                animate={pricingControls}
            >
                <h3>PRICING</h3>
                <h2>Undetermined</h2>
                <p>This section can further detail pricing models, subscription levels, and what each tier offers. Provide examples of cost-effectiveness, ROI calculations, and testimonials on how OneReturn has positively impacted other businesses financially and operationally.</p>
            </motion.div>
        </div>
    );
}

export default Landing;
