import React, { useState, useEffect, useContext, useRef } from "react";
import { AuthContext } from "../../context/AuthContext.js";
import { Link, useNavigate } from "react-router-dom";
import Cookies from 'js-cookie';
import axios from "axios";
import './navbar.css';
import { Button } from "./Button.js";
import Dropdown from "./Dropdown"

const Navbar = () => {

    //auth
    const { user, dispatch } = useContext(AuthContext);
    const isAuthenticated = !!user;
    const firstName = Cookies.get('firstName');
    const token = Cookies.get('token');

    //util
    const [click, setClick] = useState(false);
    const [dropdown, setDropdown] = useState(false);
    let navigate = useNavigate();

    const handleLogout = async (e) => {

        const response = await axios.post('http://localhost:8000/logout/', {},{
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then(response => {
            console.log(response.data);
        })
        .catch(error => {
            console.error('There was an error', error);
        });

        Cookies.remove('token');
        Cookies.remove('firstName');

        dispatch({ type: 'LOGOUT' });
        navigate('/login');
    }

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const onMouseEnter = () => {
        if(window.innerWidth < 960) {
            setDropdown(false);
        } else {
            setDropdown(true);
        }
    }


    return (
        <nav className="nav">
            <div className="navbar-logo">
                <h3>OneReturn</h3>
            </div>
            <ul>
                {!isAuthenticated && (
                    <>
                        <li><Link to="/login">Login</Link></li>
                        <li><Link to="/register">Register</Link></li>
                    </>
                )}
                {isAuthenticated && (
                    <>
                        <div className="menu-icon" onClick={handleClick}>
                            <i className={click ? 'fa-solid fa-bars' : "fa-solid fa-x"}/>
                        </div>
                        <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                            <li className="nav-item">
                                <Link to="/" className="nav-links" onClick={closeMobileMenu}>Home</Link>
                            </li>
                            <li className="nav-item">
                                <Link to="other" className="nav-links" onClick={closeMobileMenu}>Other</Link>
                            </li>
                            <Button />
                            <li className="nav-item">
                                <Link to="/profile" className="nav-links" onClick={closeMobileMenu}>Profile <i className="fa-solid fa-caret-down"/></Link>
                            </li>
                            {dropdown && <Dropdown/>}

                        </ul>
                    </>
                )}
            </ul>
        </nav>
    );
}

export default Navbar;
