import { React ,createContext, useState, useReducer} from "react";

export const DashContext = createContext();

export const DashProvider = ({ children }) => {
    const initialState = { type: "DEFAULT_BAR", visible: false };

    const reducer = (state, action) => {
        switch(action.type) {
            case 'SETTINGS_BAR':
                return { ...state, type: "SETTINGS_BAR"};
            case 'WALLET_BAR':
                return { ...state, type: "WALLET_BAR"};
            case 'SHOW_DASH':
                return { ...state, visible: true};
            case 'HIDE_DASH':
                return { ...state, visible: false};
            default:
                return state;
        }
    }

    const [dashboardState, dispatch] = useReducer(reducer, initialState);

    return (
        <DashContext.Provider value={[dashboardState, dispatch]}>
            {children}
        </DashContext.Provider>
    );
}
