import { useEffect } from "react";

export const useDropdown = (ref, callback) => {

    useEffect(() => {

        function handleOutsideClick(event){
            if(ref.current && !ref.current.contains(event.target)) {
                callback();
            }
        }
        document.addEventListener('mousedown', handleOutsideClick);

        return () => {
            document.addEventListener('mousedown', handleOutsideClick);
        };
    }, [ref, callback]);
}
