import React, {useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Cookies from "js-cookie";

const Contract = () => {

    const { contractID } = useParams();
    const [contract, setContract] = useState([]);

    const fetchData = () => {
        const token = Cookies.get('token');
        axios.get('http://localhost:8000/get/', {
            params: {
                type: 'SINGLE',
                query: contractID
            },
            headers: {
                'Authorization': "Token " + token
            }
        })
        .then((response) => {
            console.log(response);
            setContract(response.data.contracts)
        })
        .catch((error) => {
            console.error('error: ', error);
        });
    }

    useEffect(() => {
        fetchData();
     }, []);

    return (
            <div className='contractBox' key={index}>
                {deleteIndex === index && (
                    <Portal>
                        <PopupWindow label='Are you sure you want to delete this receipt?' buttonLeft='Cancel' buttonRight='Confirm' 
                        confirm={() => editReceipt('DELETE', contract.contract.contractID, contract.contract.recipientID)} toggleVisibility={() => togglePopupDelete()}/>
                    </Portal>
                )}
                {shareIndex === index && (
                    <Portal>
                        <PopupWindow label='Share' buttonLeft='Cancel' buttonRight='Share' 
                        confirm={(output) => editReceipt('SHARE', contract.contract.contractID, contract.contract.recipientID, output)} toggleVisibility={() => togglePopupShare()} 
                        textfeild={true} textfeildCaption='Enter target user email address' textfeildLabel='email@domain.com' textfeildType='email'/>
                    </Portal>
                )}
                <div className="contract-menu">
                    <div>
                        {contract.contract.flagged && (
                            <i className="info-icon" class="fa-solid fa-flag"></i>
                        )}
                        {contract.contract.sharedWith.length > 0 && (
                            <i className="info-icon" class="fa-solid fa-users"></i>
                        )}
                    </div>
                    <div>
                        <i className="contract-icon" class="fa-solid fa-share" onClick={() => {togglePopupShare(index); console.log(contract.contract.sharedWith)}}></i>
                        <i className="contract-icon" class="fa-solid fa-ellipsis-vertical" onClick={() => toggleDropdown(index)}></i>
                    </div>
                </div>
                {dropdownIndex === index && (
                        <div className="contract-dropdown">
                            <ul>
                                <li onClick={() => editReceipt('FLAG', contract.contract.contractID, contract.contract.recipientID)}>
                                    <i class="fa-solid fa-flag"></i>
                                    <span>Flag</span>
                                </li>
                                <li>
                                    <i class="fa-solid fa-trash" onClick={() => togglePopupDelete(index)}></i>
                                    <span>Delete</span>
                                </li>
                            </ul>
                        </div>
                )}
                <p className="merchantID">{contract.contract.merchantID}</p>
                <p className="merchant-individual-location">{contract.contract.merchantLocNumber}</p>
                <p className="merchantAddress">{contract.contract.merchantAddress}</p>
                <p className="item-tag">Items:</p>
                <div className="item-list-container">
                    <ul>
                        {
                            contract.items.map((item, itemIndex) => {
                                return (
                                    <div className={item.returned ? "item-container-returned":"item-container"}>
                                        <li key={itemIndex}>
                                            <i class="fa-solid fa-arrow-left fa-2xl" 
                                            onClick={() => editReceipt('RETURN', contract.contract.contractID, contract.contract.recipientID, '', item.name, data.uid)}></i>
                                            <p className="item-name">{item.name}</p>
                                            <p>: {item.price}</p>
                                            {item.returned && (
                                                <div>
                                                    <p className="returned-label">RETURNED</p>
                                                    <p className="returned-funds">-{item.price}</p>
                                                </div>
                                            )}
                                        </li>
                                    </div>
                                )
                            })
                        }
                    </ul>
                </div>
                <div className="contract-info">
                    <p className="date-purchased">
                        {contract.contract.dateCreated}
                    </p>
                </div>
            </div>
    );
}

export default Contract;
